function get_last_rcv_time(t1, t2){
    var time_diff = '';
    var var_last_rcv_time = 'undef'
    if(!(var_status[t1]==null)){
        if(!(var_status[t1][t2]==null)){
            var_last_rcv_time = var_status[t1][t2].last_rcv_time;
            var parsed_date = new Date(var_last_rcv_time);
            var diff = now - parsed_date;

            var tag = Math.floor(diff / (1000*60*60*24));
            diff = diff % (1000*60*60*24);
            if(tag>0){time_diff += "d:" + tag;}

            var std = Math.floor(diff / (1000*60*60));
            diff = diff % (1000*60*60);
            if(std>0){time_diff += " h:" + std;}

            var min = Math.floor(diff / (1000*60));
            diff = diff % (1000*60);
            if(min>0){time_diff += " m:" + min;}

            var sec = Math.floor(diff / 1000);
            time_diff += " s:" + sec;
            //var mSec = diff % 1000;
            //time_diff += "." + mSec;

            //time_diff += " h: " + std + " m: " + min + " s: " + sec + " ms: " + mSec
            //node.warn(time_diff);
        }
    }
    dataset.push([t1, t2, var_last_rcv_time, time_diff]);
}

